import { useState } from "react"
import Card from "./Card"
function Cards(){
    const [elements, setElements] = useState([
    { id: 1, img: "/img/1.jpg" , stat: "correct"},
    { id: 1, img: "/img/1.jpg" , stat: ""},
    { id: 2, img: "/img/2.jpg" , stat: ""},
    { id: 2, img: "/img/2.jpg" , stat: ""},
    { id: 3, img: "/img/3.jpg" , stat: ""},
    { id: 3, img: "/img/3.jpg" , stat: ""},
    { id: 4, img: "/img/4.jpg" , stat: ""},
    { id: 4, img: "/img/4.jpg" , stat: ""},
    { id: 5, img: "/img/5.jpg" , stat: ""},
    { id: 5, img: "/img/5.jpg" , stat: ""},
    ].sort(() => Math.random() - 0.5))

    function handleClick(id){
       element[id].stat ="active"
       setElements([... elements])
    }
    return(
    <div className="container">      
        { elements.map((element, index) => (
            <Card element={element} id={index+1} handleClick={handleClick}
            />
        ))}
     </div>
    )
}

export default Cards
